function startFadeM(e,t){if(isNaN(e)||isNaN(t)){alert("The FadeM settings require numbers. Looks like you typed "+e+" and "+t+". Oops!")}var n=parseInt(e);var r=parseInt(t);var i=$(".fadeclass").toArray();for(var s=0;s<i.length;s++){$(i[s]).css("transition","all 0.5s ease-in-out");$(i[s]).css("opacity","0."+n);$(i[s]).css("filter","aplha(opacity="+n+"0)")}window.onscroll=function(e){var t=document.documentElement,i=document.body;var s=t&&t.scrollTop||i&&i.scrollTop||0;var o=$(".fadeclass").toArray();for(var u=0;u<o.length;u++){posF=$(o[u]).offset().top-r;if(s>posF){$(o[u]).css("opacity","1.0");$(o[u]).css("filter","aplha(opacity=100)")}else{$(o[u]).css("opacity","0."+n);$(o[u]).css("filter","aplha(opacity="+n+"0)")}}}}
