function startFadeM(e,t,n){if(isNaN(e)||isNaN(t)||isNaN(n)){alert("The FadeM settings require numbers. Looks like you typed "+e+" and "+t+" and "+n+". Oops!")}var r=parseInt(e);var i=parseInt(t);var s=parseInt(n);var o=$(".fadeM").toArray();for(var u=0;u<o.length;u++){$(o[u]).css("transition","all 0."+s+"s ease-in-out");$(o[u]).css("opacity","0."+r);$(o[u]).css("filter","aplha(opacity="+r+"0)")}var a=$(".glideM").toArray();for(var u=0;u<a.length;u++){$(a[u]).css("transition","all 0."+s+"s ease-in-out");$(a[u]).css("transform","translate(-"+$(window).width()+"px,0px)")}var f=$(".stickM").toArray();var l=[];for(var u=0;u<f.length;u++){$(f[u]).css("position","relative");$(f[u]).css("z-index","100000");l.push($(f[u]).offset().top)}$(window).scroll(function(){var e=document.documentElement,t=document.body;var n=e&&e.scrollTop||t&&t.scrollTop||0;var s=$(".fadeM").toArray();for(var o=0;o<s.length;o++){posF=$(s[o]).offset().top-i;if(n>posF){$(s[o]).css("opacity","1.0");$(s[o]).css("filter","aplha(opacity=100)")}else{$(s[o]).css("opacity","0."+r);$(s[o]).css("filter","aplha(opacity="+r+"0)")}}var u=$(".glideM").toArray();for(var o=0;o<u.length;o++){posG=$(u[o]).offset().top-i;if(n>posG){$(u[o]).css("transform","translate(0px,0px)")}else{$(u[o]).css("transform","translate(-"+$(window).width()+"px,0px)")}}for(var o=0;o<f.length;o++){if(n>=l[o]){$(f[o]).css("top",n-l[o]+"px")}}})}document.write('<style type="text/css">body{display:none}</style>');jQuery(function(e){e("body").css("display","block")})
