function startFadeM(e,t){if(isNaN(e)||isNaN(t)){alert("The FadeM settings require numbers. Looks like you typed "+e+" and "+t+". Oops!")}var n=parseInt(e);var r=parseInt(t);var i=$(".fadeclass").toArray();for(var s=0;s<i.length;s++){$(i[s]).css("transition","all 0.5s ease-in-out");$(i[s]).css("opacity","0."+n);$(i[s]).css("filter","aplha(opacity="+n+"0)")}$(window).scroll(function(){var e=document.documentElement,t=document.body;var i=e&&e.scrollTop||t&&t.scrollTop||0;var s=$(".fadeclass").toArray();for(var o=0;o<s.length;o++){posF=$(s[o]).offset().top-r;if(i>posF){$(s[o]).css("opacity","1.0");$(s[o]).css("filter","aplha(opacity=100)")}else{$(s[o]).css("opacity","0."+n);$(s[o]).css("filter","aplha(opacity="+n+"0)")}}})}
